#include <vector>
#include <set>
#include <unordered_set>
#include <map>
#include <iostream>
#include <cmath>

using namespace std;
    
bool repeatedSubstringPattern(string s) {
        
        string s_copy = s;
        s_copy += s;
        
        string temp = "";
        for (int i=1; i<s_copy.length()-1; i++) temp += s_copy[i];
        s_copy = temp; // s_copy has the first and last chars removed
        std::cout << "s_copy: " << s_copy << std::endl;
        
        // if s appears in s_copy return true
        // otherwise return false
        int appearance = 0;
        
        for (int i=0; i<s.length(); i++) { //i will point to each element of s
            int count = 0;
            if (s_copy[i] == s[0]) {
                appearance = i;
                count++;
            }
            for (int j=1; j<s.length(); j++) {
                if (s_copy[appearance + j] == s[j]) count++;
            }
            if (count == s.length()) return true;
            std::cout << "count is " << count << "but not successful" << std::endl;
        }
        return false;
}

int main() {
    
    string s = "";
    std::cout << repeatedSubstringPattern(s) << std::endl;
    
    return 0;
}
